mapper.select:
SELECT userNo, userId, userPw, userName, userGender, userCall, userAddress, userEmail, userType, joinDate, lastLoginDate, account_status
FROM users
WHERE userId = #{userId}

mapper.update:
UPDATE users
SET lastLoginDate = CURRENT_TIMESTAMP
WHERE userId = #{userId}

UPDATE users
SET userPw = #{userPw}
WHERE userId = #{userId}

mapper.select:
SELECT role_id
FROM user_roles
WHERE userId = #{userId}

mapper.update:
UPDATE USERS
SET ACCOUNT_STATUS = #{accountStatus}
WHERE USERID = #{userId}
